# 创建关键字表
CREATE TABLE `goods_key` (
  `key_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `key_name` varchar(30) NOT NULL,
  PRIMARY KEY (`key_id`),
  UNIQUE KEY `key_name` (`key_name`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

# 关键词表插入数据
insert  into `goods_key`(`key_id`,`key_name`) values (1,'华为'),(2,'华为 pro'),(3,'mate'),(4,'mate 9'),(5,'液晶电视'),(6,'液晶'),(7,'液晶显示器'),(8,'显示器');



# 创建商品表
CREATE TABLE `sp_goods` (
  `goods_id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `goods_name` varchar(128) NOT NULL,
  `goods_price` decimal(10,2) NOT NULL,
  `goods_vip_price` decimal(10,2) NOT NULL,
  `goods_num` smallint(5) unsigned NOT NULL,
  `goods_weight` mediumint(9) NOT NULL,
  `goods_introduce` text NOT NULL,
  `goods_small_logo` varchar(200) NOT NULL,
  `goods_cateid` smallint(5) unsigned NOT NULL,
  `goods_brandid` smallint(5) unsigned NOT NULL,
  `goods_tj` tinyint(4) NOT NULL DEFAULT '1' COMMENT '是否推荐 1推荐 2不推荐',
  `goods_addtime` int(10) unsigned NOT NULL,
  `goods_udptime` int(10) unsigned NOT NULL,
  `goods_saletime` int(10) unsigned NOT NULL,
  `goods_isdel` enum('上架','下架') NOT NULL DEFAULT '上架',
  PRIMARY KEY (`goods_id`),
  UNIQUE KEY `goods_name` (`goods_name`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

